mapper.select:
SELECT A.IN_NO, TO_CHAR(A.IN_DATE, 'yyyy-MM-dd') AS IN_DATE, A.MTR_CD, B.MTR_NM, B.UNIT, A.ORD_NO, A.FLT_AMT, A.IN_AMT, A.UNIT_COST, A.TOT_COST, FN_GET_BAS_DTL_CD_TO_NM(B.COMP_CD) AS COMP_NM FROM MTR_IN A, ADM_MTR B WHERE A.MTR_CD = B.MTR_CD
AND TO_CHAR(A.IN_DATE, 'yyyy-MM-dd') >= #{startDate}
AND TO_CHAR(A.IN_DATE, 'yyyy-MM-dd') <= #{endDate}
AND B.COMP_CD = #{compCd}
AND B.MTR_CD = #{ditemCode}

mapper.update:
UPDATE MTR_ORD
SET
	IN_AMT = #{inAmt},
	NOTIN_AMT = (SELECT ORD_AMT FROM MTR_ORD WHERE ORD_NO = #{ordNo})-#{inAmt}
WHERE
	ORD_NO = #{ordNo}

update MTR_LOT_STCK
set 
HLD_CNT = #{hldCnt},
STCK_CNT = STCK_CNT-#{hldCnt}
WHERE 
MTR_LOT = #{mtrLot}
